import 'package:flutter/material.dart';
import 'package:projectesports/widgets/TeamListPage.dart';
import 'package:projectesports/widgets/title.dart';

import '../classes/team.dart';

class GamePage extends StatelessWidget {
  List<Team> tList;
  GamePage({super.key, required this.tList});

  List<String> gameList = [
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/png;base64,',
    'data:image/png;base64,'
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.black45,
      appBar: AppBar(
        backgroundColor: Colors.grey,
        title: const Text(
            'eStats',
            style: TextStyle(
                color: Colors.white,
                fontSize: 22
            )
        ),
      ),
      body: SafeArea(
        top: true,
        child: Column(
          children: [
            titleWidget(title: 'Games'),
            Expanded(
                child: GridView(
                  padding: EdgeInsets.zero,
                  gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                    crossAxisCount: 2,
                    crossAxisSpacing: 5,
                    mainAxisSpacing: 5,
                    childAspectRatio: 1,
                  ),
                  children: [
                    for(int i = 0; i < gameList.length; i++)...[
                      GestureDetector(
                        onTap: (){
                          Navigator.push(
                            context,
                            MaterialPageRoute(builder: (context) => teamList(tList: tList)),
                          );
                        }, // add so it moves to that game page with teams
                        child: ClipRRect(
                          borderRadius: BorderRadius.circular(8),
                          child: Image.network(
                            '${gameList[i]}',
                            width: 150,
                            height: 150,
                            fit: BoxFit.scaleDown,
                          ),
                        ),
                      ),
                    ]

                  ],
                ))
          ],
        ),
      ),
    );
  }
}
